    <script src="https://cdnjs.cloudflare.com/ajax/libs/jqueryui/1.12.1/jquery-ui.min.js"
            integrity="sha512-uto9mlQzrs59VwILcLiRYeLKPPbS/bT71da/OEBYEwcdNUk8jYIy+D176RYoop1Da+f9mvkYrmj5MCLZWEtQuA=="
            crossorigin="anonymous" referrerpolicy="no-referrer"></script>
    <script>
        $(function () {
            $('#from_month').datepicker({
                changeMonth: true,
                changeYear: true,
                showButtonPanel: true,
                dateFormat: 'MM yy',
                onClose: function (dateText, inst) {
                    $(this).datepicker('setDate', new Date(inst.selectedYear, inst.selectedMonth, 1));
                }
            });
            $('#to_month').datepicker({
                changeMonth: true,
                changeYear: true,
                showButtonPanel: true,
                dateFormat: 'MM yy',
                onClose: function (dateText, inst) {
                    $(this).datepicker('setDate', new Date(inst.selectedYear, inst.selectedMonth, 1));
                }
            });
        });
        /*$(function () {
            $('#date_range_filter').daterangepicker({
                opens: 'left'
            }, function (start, end, label) {
                console.log("A new date selection was made: " + start.format('YYYY-MM-DD') + ' to ' + end.format('YYYY-MM-DD'));
            });
        });*/
        $('#date_range_filter').daterangepicker({
            autoUpdateInput: false,
            locale: {
                cancelLabel: 'Clear'
            }
        });
        var record_id = 0;
        var dataTable = $('#salesRecordTable').DataTable({
            "processing": true,
            "serverSide": true,
            "responsive": true,
            "scrollX": true,
            "order": [[0, "DESC"]],
            "bLengthChange": false,
            "ajax": {
                url: "{{route('front.sales_record.listing')}}",
                "data": function (d) {
                    d.date_range = $('#date_range_filter').val();
                }
            },
            "language": {
                "paginate": {
                    "previous": "<",
                    "next": ">"
                },
                "search": '',
                "searchPlaceholder": 'Search Here',
            },
            "fnInfoCallback": function (oSettings, iStart, iEnd, iMax, iTotal, sPre) {
                return "Showing <span>" + iStart + " - " + iEnd + "</span> from <span>" + iMax + "</span>";
            },
            "columns": [
                {"data": "id"},
                {"data": "created_at"},
                {"data": "date"},
                {"data": "registration_number"},
                {"data": "type"},
                {"data": "cost_price"},
                {"data": "sales_price"},
                {"data": "profit", searchable: false, sortable: false},
                {"data": "payment_type"},
                {"data": "status"},
                {"data": "action", searchable: false, sortable: false}
            ],
            "drawCallback": function (settings) {
                $("#salesRecordTable_info").append('<div class="wd-sl-custometotal"><div class="count-text wd-total">Total Cost Price <span>' + settings.json.total_cost_price + '</span></div><div class="count-text wd-total">Total Sale Price <span>' + settings.json.total_sales_price + '</span></div></div>');
                $('.car_sold_counter').html(settings.json.car_sold);
                $('.cancel_deal_counter').html(settings.json.cancel_deal);
                $('.deposit_counter').html(settings.json.deposit);
                $('.cash_payment_counter').html(settings.json.total_cash_payment);
                $('.card_payment_counter').html(settings.json.total_card_payment);
            }
        });
        $("#searchbox").keyup(function () {
            dataTable.search(this.value).draw();
        });
        $('#date_range_filter').on('apply.daterangepicker', function (ev, picker) {
            $(this).val(picker.startDate.format('MM/DD/YYYY') + '-' + picker.endDate.format('MM/DD/YYYY'));
            dataTable.draw();
        });

        $('#date_range_filter').on('cancel.daterangepicker', function (ev, picker) {
            $(this).val('');
            dataTable.draw();
        });

        function get_edit_form(id) {
            $.ajax({
                type: 'get',
                data: {'id': id},
                url: "{{route('front.sales_record.get_edit_form')}}",
                beforeSend: addOverlay,
                success: function (r) {
                    removeOverlay();
                    $(".edit_sale_record_modal_content").html(r);
                    $("#editSalesRecord").modal('show');

                }
            })
        }

        function get_view(id) {
            $.ajax({
                type: 'get',
                data: {'id': id},
                url: "{{route('front.sales_record.get_view_form')}}",
                beforeSend: addOverlay,
                success: function (r) {
                    removeOverlay();
                    $(".view_sale_record_modal_content").html(r);
                    $("#viewSaleRecord").modal('show');
                }
            })
        }

        function delete_record(id) {
            record_id = id;
            $("#deleteSalesRecordModel").modal('show');
        }

        $("#frmCancelRecord").validate({
            rules: {
                reason_of_cancel: {required: true},
            },
            messages: {
                reason_of_cancel: {required: "{{__('Please enter reason of cancel')}}"}
            }
        });
        $("#btnCancelRecord").click(function () {
            if ($("#frmCancelRecord").valid()) {
                $.ajax({
                    type: 'get',
                    data: {'id': record_id, 'reason_of_cancel': $("#reason_of_cancel").val()},
                    url: "{{route('front.sales_record.delete_sale_record')}}",
                    beforeSend: addOverlay,
                    success: function (r) {
                        removeOverlay();
                        if (r.status == 200) {
                            record_id = 0;
                            $("#deleteSalesRecordModel").modal('hide');
                            $("#frmCancelRecord")[0].reset();
                            toastr['success'](r.message, "success");
                            dataTable.draw();
                        } else {
                            toastr['error'](r.message, "error");
                        }

                    }
                })
            }
        });
        $("#btnCalculate").click(function () {
            $.ajax({
                type: 'post',
                data: {'from_month': $("#from_month").val(), 'to_month': $("#to_month").val()},
                headers: {
                    'X-CSRF-TOKEN': '{{ csrf_token() }}'
                },
                url: "{{route('front.sales_record.get_comparision_data')}}",
                beforeSend: addOverlay,
                dataType: 'json',
                success: function (r) {
                    removeOverlay();
                    if (r.status == 200) {
                        console.log(r.data);
                        $(".comparision_result").html(r.data);
                    } else {
                        toastr['error'](r.message, "error");
                    }

                }
            })
        });
    </script>
